import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useAuth } from './AuthContext'; // Import useAuth to access login function

const Signup = () => {
    const navigate = useNavigate();
    const { login } = useAuth(); // Get the login function from AuthContext

    const initialValues = { email: '', password: '' };

    const validationSchema = Yup.object({
        email: Yup.string().email('Invalid email format').required('Required'),
        password: Yup.string()
            .min(8, 'Password must be at least 8 characters')
            .matches(/[A-Z]/, 'Must contain at least one uppercase letter')
            .matches(/[a-z]/, 'Must contain at least one lowercase letter')
            .matches(/[0-9]/, 'Must contain at least one number')
            .matches(/[\W_]/, 'Must contain at least one special character')
            .required('Required'),
    });

    const handleSubmit = async (values) => {
        try {
            const response = await axios.post('https://api.freeapi.app/users/register', values);
            // Assuming the response contains user data and token
            const { user, token } = response.data; // Adjust based on your API response structure
            login(user, token); // Save user and token in context
            navigate('/login'); // Redirect to login after successful signup
        } catch (error) {
            console.error('Signup failed:', error);
        }
    };

    return (
        <div className="flex items-center justify-center min-h-screen bg-gray-100">
            <div className="w-full max-w-md p-8 bg-white rounded-lg shadow-md">
                <h2 className="text-2xl font-bold text-center">Signup</h2>
                <Formik initialValues={initialValues} validationSchema={validationSchema} onSubmit={handleSubmit}>
                    <Form>
                        <div className="mb-4">
                            <label className="block text-sm font-medium text-gray-700">Email</label>
                            <Field type="email" name="email" className="mt-1 block w-full p-2 border border-gray-300 rounded-md" />
                            <ErrorMessage name="email" component="div" className="text-red-500 text-sm" />
                        </div>
                        <div className="mb-4">
                            <label className="block text-sm font-medium text-gray-700">Password</label>
                            <Field type="password" name="password" className="mt-1 block w-full p-2 border border-gray-300 rounded-md" />
                            <ErrorMessage name="password" component="div" className="text-red-500 text-sm" />
                        </div>
                        <button type="submit" className="w-full py-2 mt-4 text-white bg-blue-600 rounded-md hover:bg-blue-700">
                            Signup
                        </button>
                    </Form>
                </Formik>
            </div>
        </div>
    );
};

export default Signup;
