import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState({ token: null, user: null });
    const navigate = useNavigate();

    const getToken = () => {
        return localStorage.getItem('token');
    };

    const handleLogin = async (credentials) => {
        try {
            const response = await axios.post('https://api.freeapi.app/api/v1/users/login', credentials);
            const { user, token } = response.data.data;

            setAuth({ token, user });
            localStorage.setItem('token', token);
            localStorage.setItem('user', JSON.stringify(user));
            navigate('/dashboard');
        } catch (error) {
            console.error('Login failed:', error);
            throw error; // Allow the error to propagate for handling in the Login component
        }
    };

    const logout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        setAuth({ user: null, token: null });
        navigate('/login');
    };

    const refreshToken = async () => {
        const token = getToken();
        if (token) {
            try {
                const response = await axios.get('https://api.freeapi.app/api/v1/users/refresh-token', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                const newToken = response.data.token;
                localStorage.setItem('token', newToken);
                setAuth((prev) => ({ ...prev, token: newToken }));
            } catch (error) {
                console.error('Failed to refresh token:', error);
                logout();
            }
        }
    };

    useEffect(() => {
        const token = getToken();
        if (token) {
            setAuth({ token });
            // Optionally fetch user data here
        }
    }, []);

    return (
        <AuthContext.Provider value={{ auth, handleLogin, logout, refreshToken }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
