import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext';
import { useNavigate } from 'react-router-dom';
import ErrorBoundary from './ErrorBoundary';

const Dashboard = () => {
    const { auth, logout, getToken } = useAuth(); // Import getToken
    const navigate = useNavigate();
    const [quote, setQuote] = useState('');
    const [joke, setJoke] = useState('');
    const [quoteLoading, setQuoteLoading] = useState(true);
    const [jokeLoading, setJokeLoading] = useState(true);
    const [quoteError, setQuoteError] = useState(null);
    const [jokeError, setJokeError] = useState(null);
    const [profile, setProfile] = useState({
        name: auth.user?.name || '',
        email: auth.user?.email || '',
    });
    const [updatingProfile, setUpdatingProfile] = useState(false);
    const [profileUpdateSuccess, setProfileUpdateSuccess] = useState(null);

    // Demonstrating the use of getToken
    useEffect(() => {
        const token = getToken();
        console.log('Token from getToken in Dashboard:', token);
    }, [getToken]);

    // Fetch a random quote and joke
    useEffect(() => {
        const token = getToken(); // Use getToken to fetch the token
        if (!token) {
            console.error('No token found. Redirecting to login.');
            navigate('/login');
            return;
        }

        // Fetch quote
        axios
            .get('https://api.freeapi.app/api/v1/public/quotes', {
                headers: { Authorization: `Bearer ${token}` },
            })
            .then((response) => {
                setQuote(response.data.data?.[0]?.quote || 'No quote available');
            })
            .catch(() => setQuoteError('Failed to fetch quote.'))
            .finally(() => setQuoteLoading(false));

        // Fetch joke
        axios
            .get('https://api.freeapi.app/api/v1/public/randomjokes', {
                headers: { Authorization: `Bearer ${token}` },
            })
            .then((response) => {
                setJoke(response.data.joke || 'No joke available');
            })
            .catch(() => setJokeError('Failed to fetch joke.'))
            .finally(() => setJokeLoading(false));
    }, [getToken, navigate]);

    // Update profile handler
    const handleProfileUpdate = async () => {
        const token = getToken(); // Use getToken for the token
        if (!token) {
            console.error('No token found. Cannot update profile.');
            return;
        }

        setUpdatingProfile(true);
        setProfileUpdateSuccess(null);

        try {
            const response = await axios.put(
                'https://api.freeapi.app/api/v1/users/update',
                { name: profile.name, email: profile.email },
                {
                    headers: { Authorization: `Bearer ${token}` },
                }
            );
            setProfileUpdateSuccess('Profile updated successfully!');
        } catch (error) {
            console.error('Profile update failed:', error);
            setProfileUpdateSuccess('Failed to update profile. Please try again.');
        } finally {
            setUpdatingProfile(false);
        }
    };

    const handleLogout = () => {
        const token = getToken();
        if (token) {
            console.log('Logging out with token:', token);
        }
        logout();
        navigate('/login');
    };

    return (
        <ErrorBoundary>
            <div className="min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-800 dark:text-white">
                <div className="max-w-4xl mx-auto p-6">
                    <div className="bg-white dark:bg-gray-800 rounded-lg shadow-lg p-6">
                        <h1 className="text-2xl font-bold mb-4">
                            Hello, {auth.user?.name || 'User'}!
                        </h1>

                        {/* Display Quote */}
                        {quoteLoading ? (
                            <p>Loading quote...</p>
                        ) : quoteError ? (
                            <div className="mb-4 p-4 bg-red-100 dark:bg-red-900 rounded-lg text-red-800 dark:text-red-200">
                                {quoteError}
                            </div>
                        ) : (
                            <div className="mb-4 p-4 bg-blue-100 dark:bg-blue-900 rounded-lg">
                                <p className="text-lg font-semibold">Quote of the Day:</p>
                                <p>"{quote}"</p>
                            </div>
                        )}

                        {/* Display Joke */}
                        {jokeLoading ? (
                            <p>Loading joke...</p>
                        ) : jokeError ? (
                            <div className="mb-4 p-4 bg-red-100 dark:bg-red-900 rounded-lg text-red-800 dark:text-red-200">
                                {jokeError}
                            </div>
                        ) : (
                            <div className="mb-4 p-4 bg-green-100 dark:bg-green-900 rounded-lg">
                                <p className="text-lg font-semibold">Random Joke:</p>
                                <p>"{joke}"</p>
                            </div>
                        )}

                        {/* User Profile */}
                        <div className="bg-gray-100 dark:bg-gray-700 p-4 rounded-lg">
                            <h2 className="text-xl font-bold mb-4">Your Profile</h2>
                            <p><strong>Email:</strong> {auth.user?.email || 'N/A'}</p>
                            <p><strong>Signup Date:</strong> {auth.user?.signupDate || 'N/A'}</p>
                        </div>

                        {/* Profile Update Section */}
                        <div className="bg-gray-100 dark:bg-gray-700 p-4 rounded-lg mt-6">
                            <h2 className="text-xl font-bold mb-4">Update Profile</h2>
                            <form
                                onSubmit={(e) => {
                                    e.preventDefault();
                                    handleProfileUpdate();
                                }}
                            >
                                <div className="mb-4">
                                    <label className="block text-sm font-medium mb-1">Name</label>
                                    <input
                                        type="text"
                                        value={profile.name}
                                        onChange={(e) =>
                                            setProfile((prev) => ({ ...prev, name: e.target.value }))
                                        }
                                        className="w-full p-2 border rounded-md dark:bg-gray-800 dark:border-gray-600"
                                    />
                                </div>
                                <div className="mb-4">
                                    <label className="block text-sm font-medium mb-1">Email</label>
                                    <input
                                        type="email"
                                        value={profile.email}
                                        onChange={(e) =>
                                            setProfile((prev) => ({ ...prev, email: e.target.value }))
                                        }
                                        className="w-full p-2 border rounded-md dark:bg-gray-800 dark:border-gray-600"
                                    />
                                </div>
                                <button
                                    type="submit"
                                    disabled={updatingProfile}
                                    className="bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 transition-all"
                                >
                                    {updatingProfile ? 'Updating...' : 'Update Profile'}
                                </button>
                            </form>
                            {profileUpdateSuccess && (
                                <p className="mt-4 text-center text-sm">
                                    {profileUpdateSuccess}
                                </p>
                            )}
                        </div>

                        {/* Logout Button */}
                        <button
                            onClick={handleLogout} // Use the handleLogout function
                            className="mt-6 w-full bg-red-600 text-white py-2 px-4 rounded-md hover:bg-red-700 transition-all"
                        >
                            Logout
                        </button>
                    </div>
                </div>
            </div>
        </ErrorBoundary>
    );
};

export default Dashboard;
