import React, { useState } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useAuth } from './AuthContext'; // Import useAuth to access login function

const Signup = () => {
    const navigate = useNavigate();
    const { login } = useAuth(); // Get the login function from AuthContext
    const [serverError, setServerError] = useState(''); // State to store server errors

    const initialValues = { username: '', email: '', password: '' }; // Added username

    const validationSchema = Yup.object({
        username: Yup.string()
            .min(3, 'Username must be at least 3 characters')
            .max(20, 'Username must not exceed 20 characters')
            .required('Required')
            .matches(/^[a-z]+$/, 'Username must be in lowercase'), // Ensure username is lowercase
        email: Yup.string().email('Invalid email format').required('Required'),
        password: Yup.string()
            .min(8, 'Password must be at least 8 characters')
            .matches(/[A-Z]/, 'Must contain at least one uppercase letter')
            .matches(/[a-z]/, 'Must contain at least one lowercase letter')
            .matches(/[0-9]/, 'Must contain at least one number')
            .matches(/[\W_]/, 'Must contain at least one special character')
            .required('Required'),
    });

    const handleSubmit = async (values) => {
        if (!validateEmail(values.email)) {
            console.error('Invalid email format');
            return;
        }
    
        try {
            const credentials = {
                email: values.email.trim(),
                password: values.password.trim(),
            };
    
            const response = await handleLogin(credentials);
            console.log({response});
            navigate('/dashboard');  // Redirect to dashboard
        } catch (error) {
            console.error('Error during login:', error);
        }
    };

    return (
        <div className="flex items-center justify-center min-h-screen bg-gray-100">
            <div className="w-full max-w-md p-8 bg-white rounded-lg shadow-md">
                <h2 className="text-2xl font-bold text-center">Signup</h2>
                {serverError && (
                    <div className="mb-4 text-red-500 text-sm text-center">
                        {serverError}
                    </div>
                )}
                <Formik initialValues={initialValues} validationSchema={validationSchema} onSubmit={handleSubmit}>
                    <Form>
                        <div className="mb-4">
                            <label className="block text-sm font-medium text-gray-700">Username</label>
                            <Field
                                type="text"
                                name="username"
                                className="mt-1 block w-full p-2 border border-gray-300 rounded-md"
                            />
                            <ErrorMessage name="username" component="div" className="text-red-500 text-sm" />
                        </div>
                        <div className="mb-4">
                            <label className="block text-sm font-medium text-gray-700">Email</label>
                            <Field
                                type="email"
                                name="email"
                                className="mt-1 block w-full p-2 border border-gray-300 rounded-md"
                            />
                            <ErrorMessage name="email" component="div" className="text-red-500 text-sm" />
                        </div>
                        <div className="mb-4">
                            <label className="block text-sm font-medium text-gray-700">Password</label>
                            <Field
                                type="password"
                                name="password"
                                className="mt-1 block w-full p-2 border border-gray-300 rounded-md"
                            />
                            <ErrorMessage name="password" component="div" className="text-red-500 text-sm" />
                        </div>
                        <button
                            type="submit"
                            className="w-full py-2 mt-4 text-white bg-blue-600 rounded-md hover:bg-blue-700 transition-all"
                        >
                            Signup
                        </button>
                    </Form>
                </Formik>
            </div>
        </div>
    );
};

export default Signup;
