import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

// Safe parse function to handle JSON parsing safely
const safeParse = (data) => {
    try {
        return JSON.parse(data);
    } catch (error) {
        console.error('Failed to parse JSON:', error);
        return null;  // Return null if JSON parsing fails
    }
};

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState({ token: null, user: null });
    const [loading, setLoading] = useState(true); // Track loading state
    const navigate = useNavigate();

    // Fetch user data using token
    const fetchUserData = async (token) => {
        try {
            const response = await axios.get('https://api.freeapi.app/api/v1/users/current-user', {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            console.log('User data:', response.data);
        } catch (error) {
            handleAuthError(error);  // Handle authentication error
        }
    };

    // Handle login functionality
    const handleLogin = async (credentials) => {
        if (!credentials.email || !credentials.password) {
            console.error('Email and password are required');
            return;
        }
    
        try {
            const response = await axios.post(
                'https://api.freeapi.app/api/v1/users/login',
                credentials,
                { headers: { 'Content-Type': 'application/json' } }
            );

            if (response.data &&  response.data.data.accessToken) {
                localStorage.setItem('token', response.data.data.accessToken);
                localStorage.setItem('user', JSON.stringify(response.data.data.user));
                console.log('Successfully logged in');
                navigate('/dashboard'); // Redirect after successful login
            }
            else {
                console.error('No token found in response');
            }
            return response;
        } catch (error) {
            console.error('Login failed:', error.response?.data?.message || error.message);
        }
    };    
   
    // Handle authentication errors (expired or invalid token)
    const handleAuthError = (error) => {
        if (
            error.response &&
            error.response.status === 401 &&
            (error.response.data.message === 'jwt malformed' || error.response.data.message.includes('expired'))
        ) {
            alert('Your session has expired or the token is invalid. Please log in again.');
            localStorage.removeItem('token');
            localStorage.removeItem('user');
            localStorage.removeItem('refreshToken');
            logout();
            navigate('/login');
        } else {
            console.error('Unexpected authentication error:', error.message);
        }
    };

    // Perform login action
    const login = (user, token, refreshToken) => {
        console.log('User:', user);  // Verify if user is received correctly
        console.log('Token:', token);  // Verify if token is received correctly
        console.log('Refresh Token:', refreshToken);  // Verify if refreshToken is received correctly

        if (user && token) {
            localStorage.setItem('token', token);
            localStorage.setItem('user', JSON.stringify(user));
            localStorage.setItem('refreshToken', refreshToken);
            setAuth({ token, user });
            console.log('Successfully logged in and stored data in localStorage.');
            console.log('Stored token:', token);  // Use the token directly
        } else {
            console.error('Token or user is missing, cannot store.');
        }
    };

    // Perform logout action
    const logout = () => {
        setAuth({ token: null, user: null });
        setLoading(false);
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        localStorage.removeItem('refreshToken');
        navigate('/login');
    };

    // Retrieve token from localStorage
    const getToken = () => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.log('No token found in localStorage');
        }
        return token;
    };

    // Check token on app load and fetch user data
    useEffect(() => {
        const storedToken = localStorage.getItem('token');
        const storedUser = safeParse(localStorage.getItem('user'));
    
        if (storedToken && storedUser) {
            // Update auth state from localStorage
            setAuth({ token: storedToken, user: storedUser });
            fetchUserData(storedToken); // Fetch user data based on token
    
            // Redirect to dashboard if already authenticated
            navigate('/dashboard');
        } else {
            console.log('No token or user found, redirecting to login');
            setLoading(false);
            navigate('/login');
        }
    }, [navigate]);    

    // Refresh token every 15 minutes
    useEffect(() => {
        const interval = setInterval(() => {
            refreshToken();
        }, 15 * 60 * 1000); // Refresh token every 15 minutes

        return () => clearInterval(interval); // Cleanup on component unmount
    }, []);

    // Handle token refresh
    const refreshToken = async () => {
        const storedRefreshToken = localStorage.getItem('refreshToken'); // Retrieve refresh token correctly
        if (!storedRefreshToken) {
            console.error('Refresh token is missing.');
            logout();
            navigate('/login');
            return;
        }        
        try {
            const response = await axios.post('https://api.freeapi.app/api/v1/users/refresh-token', { token: storedRefreshToken });
            const { accessToken } = response.data;
            localStorage.setItem('token', accessToken);
            setAuth((prev) => ({ ...prev, token: accessToken }));
        } catch (error) {
            handleAuthError(error);  // Use handleAuthError to manage the error
        }
    };

    return (
        <AuthContext.Provider value={{ auth, loading, login, logout, handleLogin }}>
            {children}
        </AuthContext.Provider>
    );
};
export const useAuth = () => useContext(AuthContext);
