import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState({ token: null, user: null });
    const navigate = useNavigate();

    const getToken = () => {
        return localStorage.getItem('token');
    };

    const handleLogin = async (credentials) => {
        try {
            // First check internet connectivity
            const online = navigator.onLine;
            if (!online) {
                throw new Error('No internet connection detected');
            }

            const response = await axios.post(
                'https://api.freeapi.app/api/v1/auth/login',
                {
                    email: credentials.email,
                    password: credentials.password
                },
                {
                    headers: {
                        'Content-Type': 'application/json',
                        'Accept': 'application/json'
                    },
                    timeout: 15000,
                    retry: 2,
                    retryDelay: 1000
                }
            );

            if (response.data?.status === 'success' && response.data?.data?.token) {
                const userData = response.data.data;
                setAuth({ token: userData.token, user: userData.user });
                localStorage.setItem('token', userData.token);
                localStorage.setItem('user', JSON.stringify(userData.user));
                return userData;
            }

            const responseData = response.data?.data;
            return responseData;

        } catch (error) {
            const errorTypes = {
                'ECONNABORTED': 'Request timeout - please try again',
                'Network Error': 'Network connection issue - please check your internet',
                'ERR_NETWORK': 'Unable to reach the server - please try again'
            };

            const errorMessage = errorTypes[error.code] || 
                               error.response?.data?.message || 
                               'Login service temporarily unavailable';

            throw new Error(errorMessage);
        }
    };
    
    const logout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        setAuth({ user: null, token: null });
        navigate('/login', { replace: true });
    };
    
    useEffect(() => {
        const token = getToken();
        if (token) {
            setAuth({ token });
            // Optionally fetch user data here
        }
    }, []);

    return (
        <AuthContext.Provider value={{ auth, handleLogin, logout, getToken }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
