import React from 'react';
import { Navigate } from 'react-router-dom';
import { useAuth } from './AuthContext'; // Assuming you are using the AuthContext

const ProtectedRoute = ({ children }) => {
    const { auth } = useAuth(); // Access the authentication state

    // Check if there is a token (meaning the user is authenticated)
    if (!auth?.token) {
        return <Navigate to="/login" />; // Redirect to login if no token
    }

    return children; // Render the children (protected content) if authenticated
};

export default ProtectedRoute;
