import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext';
import { useNavigate } from 'react-router-dom';
import ErrorBoundary from './ErrorBoundary';

const Dashboard = () => {
    const { auth, logout } = useAuth();
    const navigate = useNavigate();
    const [quote, setQuote] = useState('');
    const [joke, setJoke] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Single authentication check on mount
    useEffect(() => {
        const checkAuth = () => {
            const token = localStorage.getItem('token');
            if (!token) {
                navigate('/login', { replace: true });
            }
        };
        checkAuth();
    }, []); // Empty dependency array ensures single execution

    // Data fetching with controlled dependencies
    useEffect(() => {
        let mounted = true;
        const token = localStorage.getItem('token');

        const fetchData = async () => {
            if (!token) return;

            try {
                const [quoteResponse, jokeResponse] = await Promise.all([
                    axios.get('https://api.freeapi.app/api/v1/public/quotes', {
                        headers: { Authorization: `Bearer ${token}` }
                    }),
                    axios.get('https://api.freeapi.app/api/v1/public/randomjokes', {
                        headers: { Authorization: `Bearer ${token}` }
                    })
                ]);

                if (mounted) {
                    setQuote(quoteResponse.data.data?.[0]?.quote || 'No quote available');
                    setJoke(jokeResponse.data.data?.body || 'No joke available');
                    setLoading(false);
                }
            } catch (error) {
                if (mounted) {
                    setError('Failed to load content');
                    setLoading(false);
                }
            }
        };

        fetchData();

        return () => {
            mounted = false;
        };
    }, []); // Run once on mount

    // Rest of your component code remains the same

    return (
        <ErrorBoundary>
            <div className="min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-800 dark:text-white">
                <div className="max-w-4xl mx-auto p-6">
                    <div className="bg-white dark:bg-gray-800 rounded-lg shadow-lg p-6">
                        <h1 className="text-2xl font-bold mb-4">Hello, {auth.user?.name || 'User'}!</h1>
                        {loading ? (
                            <p>Loading content...</p>
                        ) : error ? (
                            <p className="text-red-500">{error}</p>
                        ) : (
                            <>
                                <div className="mb-4 p-4 bg-blue-100 dark:bg-blue-900 rounded-lg">
                                    <h2 className="text-lg font-semibold">Quote of the Day:</h2>
                                    <p>{quote}</p>
                                </div>
                                <div className="mb-4 p-4 bg-green-100 dark:bg-green-900 rounded-lg">
                                    <h2 className="text-lg font-semibold">Random Joke:</h2>
                                    <p>{joke}</p>
                                </div>
                            </>
                        )}
                        <button
                            onClick={logout}
                            className="mt-6 w-full bg-red-600 text-white py-2 px-4 rounded-md hover:bg-red-700 transition-all"
                        >
                            Logout
                        </button>
                    </div>
                </div>
            </div>
        </ErrorBoundary>
    );
};

export default Dashboard;
