import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState({ token: null, user: null });
    const navigate = useNavigate();

    const getToken = () => {
        return localStorage.getItem('token');
    };

    const handleLogin = async (credentials) => {
        try {
            const response = await axios.post('https://api.freeapi.app/api/v1/users/login', credentials);
            const { user, token } = response.data.data;

            const storedUserId = localStorage.getItem('userId');
            if (storedUserId && storedUserId === user.id) {
                // Proceed with successful login actions
                localStorage.setItem('user', JSON.stringify(user));
                localStorage.setItem('token', token);
                navigate('/dashboard');
            } else {
                throw new Error('Invalid user ID');
            }
        } catch (error) {
            console.error('Login failed:', error);
            setServerError(error.response?.data?.data?.message || 'Login failed. Please try again later.');
        }
    };

const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('isAuthenticated');
    setAuth({ user: null, token: null, isAuthenticated: false });
    navigate('/login', { replace: true });
};

useEffect(() => {
    const token = localStorage.getItem('token');
    const user = JSON.parse(localStorage.getItem('user'));
    const isAuthenticated = localStorage.getItem('isAuthenticated');

    if (token && user && isAuthenticated) {
        setAuth({
            token,
            user,
            isAuthenticated: true
        });
    }
}, []);

return (
    <AuthContext.Provider value={{
        auth,
        setAuth,
        handleLogin,
        getToken,
        logout
    }}>
        {children}
    </AuthContext.Provider>
);
};
export const useAuth = () => useContext(AuthContext);
