import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext';
import { useNavigate } from 'react-router-dom';
import ErrorBoundary from './ErrorBoundary';
  const Dashboard = () => {
      const { auth, logout } = useAuth();
      const navigate = useNavigate();
      const [quote, setQuote] = useState('');
      const [joke, setJoke] = useState('');
      const [loading, setLoading] = useState(true);
      const [error, setError] = useState(null);
      const [editMode, setEditMode] = useState(false);
      const [userName, setUserName] = useState(auth.user?.name || 'User');
      const [updateStatus, setUpdateStatus] = useState('');
    
      const userData = JSON.parse(localStorage.getItem('user')) || {};

      const handleUpdateName = async () => {
          try {
              const response = await axios.put(
                  'https://api.freeapi.app/api/v1/users/update',
                  { name: userName },
                  { headers: { Authorization: `Bearer ${auth.token}` }}
              );
            
              // Update localStorage
              const updatedUser = { ...userData, name: userName };
              localStorage.setItem('user', JSON.stringify(updatedUser));
            
              setEditMode(false);
              setUpdateStatus('Name updated successfully!');
            
              // Clear status message after 3 seconds
              setTimeout(() => setUpdateStatus(''), 3000);
          } catch (error) {
              setUpdateStatus('Failed to update name. Please try again.');
          }
      };

      useEffect(() => {
          let mounted = true;
          const token = localStorage.getItem('token');

          const fetchData = async () => {
              if (!token) {
                  navigate('/login', { replace: true });
                  return;
              }

              try {
                  const [quoteRes, jokeRes] = await Promise.all([
                      axios.get('https://api.freeapi.app/api/v1/public/quotes', {
                          headers: { Authorization: `Bearer ${token}` }
                      }),
                      axios.get('https://api.freeapi.app/api/v1/public/randomjokes', {
                          headers: { Authorization: `Bearer ${token}` }
                      })
                  ]);

                  if (mounted) {
                      setQuote(quoteRes.data.data?.[0]?.quote || 'No quote available');
                      setJoke(jokeRes.data.data?.body || 'No joke available');
                      setLoading(false);
                  }
              } catch (error) {
                  if (mounted) {
                      setError('Failed to load content');
                      setLoading(false);
                  }
              }
          };

          fetchData();
          return () => { mounted = false; };
      }, []);

      return (
          <ErrorBoundary>
              <div className="min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-800 dark:text-white">
                  <div className="max-w-4xl mx-auto p-6">
                      <div className="bg-white dark:bg-gray-800 rounded-lg shadow-lg p-6">
                          <div className="flex items-center justify-between mb-4">
                              {editMode ? (
                                  <div className="flex items-center gap-2">
                                      <input
                                          type="text"
                                          value={userName}
                                          onChange={(e) => setUserName(e.target.value)}
                                          className="px-3 py-1 border rounded-md dark:bg-gray-700"
                                          placeholder="Enter new name"
                                      />
                                      <button
                                          onClick={handleUpdateName}
                                          className="px-4 py-1 bg-blue-500 text-white rounded-md hover:bg-blue-600"
                                      >
                                          Save
                                      </button>
                                      <button
                                          onClick={() => setEditMode(false)}
                                          className="px-4 py-1 bg-gray-500 text-white rounded-md hover:bg-gray-600"
                                      >
                                          Cancel
                                      </button>
                                  </div>
                              ) : (
                                  <>
                                      <h1 className="text-2xl font-bold">Hello, {userName}!</h1>
                                      <button
                                          onClick={() => setEditMode(true)}
                                          className="px-4 py-1 bg-blue-500 text-white rounded-md hover:bg-blue-600"
                                      >
                                          Edit Name
                                      </button>
                                  </>
                              )}
                          </div>
                        
                          {updateStatus && (
                              <div className={`mb-4 p-2 rounded-md text-center ${
                                  updateStatus.includes('success') ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'
                              }`}>
                                  {updateStatus}
                              </div>
                          )}

                          {/* Existing user info section */}
                          <div className="mb-6 p-4 bg-gray-50 dark:bg-gray-700 rounded-lg">
                              <h2 className="text-xl font-semibold mb-3">User Information</h2>
                              <div className="space-y-2">
                                  <p><span className="font-medium">Email:</span> {userData.email}</p>
                                  <p><span className="font-medium">Member Since:</span> {userData.createdAt ? 
                                      new Date(userData.createdAt).toLocaleDateString('en-US', {
                                          year: 'numeric',
                                          month: 'long',
                                          day: 'numeric'
                                      }) : 'N/A'}
                                  </p>
                              </div>
                          </div>

                          {/* Quote and Joke Section */}
                          {loading ? (
                              <p>Loading content...</p>
                          ) : error ? (
                              <p className="text-red-500">{error}</p>
                          ) : (
                              <>
                                  <div className="mb-4 p-4 bg-blue-100 dark:bg-blue-900 rounded-lg">
                                      <h2 className="text-lg font-semibold">Quote of the Day:</h2>
                                      <p>{quote}</p>
                                  </div>
                                  <div className="mb-4 p-4 bg-green-100 dark:bg-green-900 rounded-lg">
                                      <h2 className="text-lg font-semibold">Random Joke:</h2>
                                      <p>{joke}</p>
                                  </div>
                              </>
                          )}

                          <button
                              onClick={logout}
                              className="mt-6 w-full bg-red-600 text-white py-2 rounded-md hover:bg-red-700"
                          >
                              Logout
                          </button>
                      </div>
                  </div>
              </div>
          </ErrorBoundary>
      );
  };

  export default Dashboard;
