import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState({ token: null, user: null });
    const navigate = useNavigate();

    const getToken = () => {
        return localStorage.getItem('token');
    };

    const handleLogin = async (credentials) => {
        try {
            const response = await axios.post('https://api.freeapi.app/api/v1/auth/login', credentials, {
                headers: {
                    'Content-Type': 'application/json'
                },
                timeout: 5000
            });
            
            if (response.data?.status === 'success' && response.data?.data?.token) {
                const userData = response.data.data;
                setAuth({
                    token: userData.token,
                    user: userData.user,
                    isAuthenticated: true
                });
                localStorage.setItem('token', userData.token);
                localStorage.setItem('user', JSON.stringify(userData.user));
                navigate('/dashboard');
                return userData;
            } else {
                throw new Error('Invalid credentials or server response');
            }
        } catch (error) {
            const errorMessage = error.response?.data?.message || 'Authentication failed. Please verify your credentials.';
            console.error('Login error details:', errorMessage);
            throw new Error(errorMessage);
        }
    };    

    const logout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        localStorage.removeItem('isAuthenticated');
        setAuth({ user: null, token: null, isAuthenticated: false });
        navigate('/login', { replace: true });
    };    

    useEffect(() => {
        const token = localStorage.getItem('token');
        const user = JSON.parse(localStorage.getItem('user'));
        const isAuthenticated = localStorage.getItem('isAuthenticated');

        if (token && user && isAuthenticated) {
            setAuth({
                token,
                user,
                isAuthenticated: true
            });
        }
    }, []);

    return (
        <AuthContext.Provider value={{
            auth,
            setAuth,
            handleLogin,
            getToken,
            logout
        }}>
            {children}
        </AuthContext.Provider>
    );
};
export const useAuth = () => useContext(AuthContext);
