import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState({ token: null, user: null });
    const [loading, setLoading] = useState(true); // Start with loading as true until token is checked

    // Fetch user data using token
    const fetchUserData = async (token) => {
        try {
            const response = await axios.get('https://api.freeapi.app/api/v1/users/current-user', {
                headers: { Authorization: `Bearer ${token}` },
            });
            setAuth((prev) => ({
                ...prev,
                user: response.data, // Set user data directly
            }));
        } catch (error) {
            console.error('Failed to fetch user data:', error);
            handleAuthError(error);
        } finally {
            setLoading(false);
        }
    };

    // Handle authentication errors
    const handleAuthError = (error) => {
        if (
            error.response &&
            error.response.status === 401 &&
            (error.response.data.message === 'jwt malformed' || error.response.data.message.includes('expired'))
        ) {
            alert('Your session has expired or the token is invalid. Please log in again.');
            logout();
        } else {
            console.error('Unexpected authentication error:', error.message);
        }
    };

    const handleLogin = async (credentials) => {
        try {
            const response = await axios.post('https://api.freeapi.app/api/v1/users/login', credentials);
            console.log('Login response:', response.data); // Check the response data
            const { user, token } = response.data; // Destructure token and user from response
            if (!user || !token) {
                throw new Error('Invalid response format');
            }
            login(user, token); // Save user and token in state
        } catch (error) {
            console.error('Login failed:', error.response?.data || error.message);
            alert(error.response?.data?.message || 'Login failed. Please try again.');
        }
    };
    

    // Perform login action
    const login = async (user, token) => {
        try {
            setAuth({ token, user });
            localStorage.setItem('token', token); // Store token in localStorage
            localStorage.setItem('user', JSON.stringify(user)); // Store user in localStorage (if needed)
            console.log('Token stored in localStorage:', localStorage.getItem('token')); // Verify token is stored
        } catch (error) {
            console.error('Login failed:', error);
        }
    };

    // Perform logout action
    const logout = () => {
        setAuth({ token: null, user: null });
        setLoading(false);
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        console.log('Token removed from localStorage:', localStorage.getItem('token')); // Confirm token removal
    };

    // Retrieve token from localStorage
    const getToken = () => {
        const token = localStorage.getItem('token');
        console.log('Retrieved token from localStorage:', token); // Log token
        return token;
    };

    // Check token on app load
    useEffect(() => {
        const storedToken = localStorage.getItem('token');
        console.log("Token from localStorage:", storedToken); // Log token on page load
    
        if (storedToken) {
            setAuth((prev) => ({
                ...prev,
                token: storedToken,
            }));
            fetchUserData(storedToken); // Fetch user data if token exists
        } else {
            setLoading(false);  // Mark loading as complete if no token exists
        }
    }, []);
    
    // Refresh token every 15 minutes
    useEffect(() => {
        const interval = setInterval(() => {
            refreshToken();
        }, 15 * 60 * 1000); // Refresh token every 15 minutes

        return () => clearInterval(interval);
    }, []);

    // Handle token refresh
    const refreshToken = async () => {
        const storedRefreshToken = localStorage.getItem('refreshToken');
        if (!storedRefreshToken) {
            console.error('Refresh token is missing.');
            return;
        }
        try {
            const response = await axios.post('https://api.freeapi.app/api/v1/users/refresh-token', { token: storedRefreshToken });
            const { accessToken } = response.data;

            setAuth((prev) => ({
                ...prev,
                token: accessToken,
            }));

            // Update token in localStorage
            localStorage.setItem('token', accessToken);
        } catch (error) {
            console.error('Token refresh failed:', error);
            logout(); // Log out if refresh fails
        }
    };

    return (
        <AuthContext.Provider value={{ auth, loading, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
