import React from 'react';
import { Navigate } from 'react-router-dom';
import { useAuth } from './AuthContext';

const ProtectedRoute = ({ children }) => {
    const { auth, loading } = useAuth();
    const { getToken } = useAuth(); // Get the getToken function from AuthContext
    const token = getToken(); // Retrieve the token

    // Show loading while waiting for auth state to load
    if (loading) {
        return <div>Loading...</div>; // You can replace this with a loading spinner
    }

    // If no token or auth is available, redirect to login
    if (!auth?.token || !token) {
        console.warn('No token found. Redirecting to login.'); // Log a warning if no token is found
        return <Navigate to="/login" />;
    }

    return children; // Render the protected route if authenticated
};

export default ProtectedRoute;
