import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState({ token: null, user: null });
    const navigate = useNavigate();

    const getToken = () => {
        return localStorage.getItem('token');
    };

    const handleLogin = async (credentials) => {
        try {
            const response = await axios.post(
                'https://api.freeapi.app/api/v1/auth/sign-in',  // Updated endpoint
                {
                    email: credentials.email,
                    password: credentials.password
                },
                {
                    headers: {
                        'Content-Type': 'application/json'
                    }
                }
            );

            // Enhanced success handling
            if (response.status === 200 && response.data?.data?.token) {
                const userData = response.data.data;
                setAuth({ token: userData.token, user: userData.user });
                localStorage.setItem('token', userData.token);
                localStorage.setItem('user', JSON.stringify(userData.user));
                return userData;
            }

            throw new Error('Invalid credentials provided');
        } catch (error) {
            // Specific error messages based on response
            const errorMessage = 
                error.response?.status === 401 ? 'Invalid email or password' :
                error.response?.status === 404 ? 'Account not found' :
                'Authentication service unavailable';
                
            throw new Error(errorMessage);
        }
    };
    
    const logout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        setAuth({ user: null, token: null });
        navigate('/login', { replace: true });
    };
    
    useEffect(() => {
        const token = getToken();
        if (token) {
            setAuth({ token });
            // Optionally fetch user data here
        }
    }, []);

    return (
        <AuthContext.Provider value={{ auth, handleLogin, logout, getToken }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
