import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState({ user: null, token: null });
    const [loading, setLoading] = useState(false);

    const fetchUserData = async (token) => {
        setLoading(true);
        try {
            const response = await axios.get('https://api.freeapi.app/api/v1/users/current-user', {
                headers: { Authorization: `Bearer ${token}` },
            });
            setAuth((prevAuth) => ({
                ...prevAuth,
                user: response.data,
            }));
        } catch (error) {
            console.error('Failed to fetch user data:', error);
            handleAuthError(error);
        } finally {
            setLoading(false);
        }
    };

    const handleAuthError = (error) => {
        if (
            error.response &&
            error.response.status === 401 &&
            (error.response.data.message === 'jwt malformed' || error.response.data.message.includes('expired'))
        ) {
            alert('Your session has expired or the token is invalid. Please log in again.');
            logout();
        } else {
            console.error('Unexpected authentication error:', error.message);
        }
    };

    const handleLogin = async (credentials) => {
        console.log('Login credentials:', credentials);
        try {
            const response = await axios.post('https://api.freeapi.app/api/v1/users/login', credentials);
            console.log('Login response:', response.data);
    
            // Extract and set the token and user
            const { user, token } = response.data;
            if (!user || !token) {
                throw new Error('Invalid response format');
            }
    
            login(user, token); // Save in context
        } catch (error) {
            console.error('Login failed:', error.response?.data || error.message);
            alert(error.response?.data?.message || 'Login failed. Please try again.');
        }
    };
    
    
    const login = (user, token) => {
        console.log('Login function called with:', { user, token });

        if (!token || token.trim() === '') {
            console.error('Invalid token provided during login.');
            alert('Login failed. Please try again.');
            return;
        }

        setAuth({ user, token });
        localStorage.setItem('token', token);
        localStorage.setItem('user', JSON.stringify(user));

        fetchUserData(token);
    };

    const logout = () => {
        setAuth({ user: null, token: null });
        localStorage.removeItem('token');
        localStorage.removeItem('user');
    };

    const refreshToken = async () => {
        const token = localStorage.getItem('token');
        if (token) {
            try {
                const response = await axios.post('https://api.freeapi.app/users/refresh-token', { token });
                const newToken = response.data.token;
                if (!newToken || newToken.trim() === '') {
                    throw new Error('Empty token received from refresh API.');
                }
                setAuth((prev) => ({ ...prev, token: newToken }));
                localStorage.setItem('token', newToken);
            } catch (error) {
                console.error('Token refresh failed:', error);
                handleAuthError(error);
            }
        }
    };

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            fetchUserData(token);
        }
    }, []);

    useEffect(() => {
        const interval = setInterval(() => {
            refreshToken();
        }, 15 * 60 * 1000);

        return () => clearInterval(interval);
    }, []);

    return (
        <AuthContext.Provider value={{ auth, login: handleLogin, logout, loading }}>
            {children}
        </AuthContext.Provider>
    );    
};

export const useAuth = () => {
    return useContext(AuthContext);
};
