import React from 'react';
import { Navigate } from 'react-router-dom';
import { useAuth } from './AuthContext'; // Assuming you are using the AuthContext

const ProtectedRoute = ({ children }) => {
    const { auth } = useAuth(); // Access the authentication state

    if (!auth.token) { // If there's no token, redirect to login
        return <Navigate to="/login" />;
    }

    return children; // If authenticated, render the children
};

export default ProtectedRoute;
