import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState(() => {
        const storedAuth = localStorage.getItem('auth');
        return storedAuth ? JSON.parse(storedAuth) : {
            user: null,
            userId: null,
            isAuthenticated: false
        };
    });

    const [serverError, setServerError] = useState(null);
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const getuserId = () => {
        return localStorage.getItem('userId'); 
    };    

    const handleRegister = async (values) => {
        setLoading(true);
        try {
            const response = await axios.post('https://api.freeapi.app/api/v1/users/register', values);

            if (response.data.success) {
                const { user, userId} = response.data.data;

                localStorage.setItem('user', JSON.stringify(user));
                localStorage.setItem('userId', user._id);
                setAuth({ user, userId, isAuthenticated: true });
                console.log(response.data);
            navigate('/dashboard');
            } else {
                throw new Error(response.data.message || 'Registration failed');
            }
        } catch (error) {
            console.error('Signup failed:', error.message);
            alert(error.message || 'An error occurred during registration');
        }
        finally {
            setLoading(false);
        }
    };

    const handleLogin = async (credentials) => {
        try {
            const response = await axios.post('https://api.freeapi.app/api/v1/users/login', credentials);
            if (response.data.success) {
                const { user, userId } = response.data.data;

                localStorage.setItem('user', JSON.stringify(user));
                localStorage.setItem('userId', user._id);

                setAuth({ user, userId: user._id, isAuthenticated: true });
                navigate('/dashboard');
                console.log('Login successful:', user);
            } else {
                throw new Error(response.data.message || 'Login failed');
            }
        } catch (error) {
            if (error.response?.status === 404) {
                alert('User does not exist. Please register first.');
            } else {
                console.error('Login failed:', error.message);
                alert(error.message || 'An error occurred during login.');
            }
        }
    };

    useEffect(() => {
        const storedUser = localStorage.getItem('user');
        const storedUserId = localStorage.getItem('userId');
        if (storedUser && storedUserId) {
            setAuth({
                user: JSON.parse(storedUser),
                userId: storedUserId,
                isAuthenticated: true,
            });
        }
    }, []);

    useEffect(() => {
        if (auth) {
            localStorage.setItem('auth', JSON.stringify(auth));
        } else {
            localStorage.removeItem('auth');
        }
    }, [auth]);

    const logout = () => {
        localStorage.removeItem('user');
        localStorage.removeItem('userId');
        setAuth({ user: null, userId: null, isAuthenticated: false });

        navigate('/login');
    };

    useEffect(() => {
        console.log("User authentication status:", auth.isAuthenticated);
        const userId = localStorage.getItem('userId');
        const user = JSON.parse(localStorage.getItem('user'));

        if (userId && user) {
            setAuth({
                userId,
                user,
                isAuthenticated: true
            });
        } else {
            setAuth({
                userId: null,
                user: null,
                isAuthenticated: false
            });
        }
    }, []);

    return (
        <AuthContext.Provider value={{
            auth,
            setAuth,
            handleRegister,
            handleLogin,
            logout,
            loading,
            setLoading,
            serverError,
            setServerError,
            getuserId
        }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
