import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider } from './AuthContext'; // Importing your AuthContext provider
import ErrorBoundary from './ErrorBoundary'; // Importing ErrorBoundary
import Login from './Login'; // Login component
import Signup from './Signup'; // Signup component
import Dashboard from './Dashboard'; // Dashboard component
import ProtectedRoute from './ProtectedRoute'; // ProtectedRoute component

const App = () => {
    return (
        <Router> {/* Wrap your entire app with Router */}
            <ErrorBoundary>
                <AuthProvider> {/* Wrap with AuthProvider for context */}
                    <Routes>
                        {/* Public Routes */}
                        <Route path="/login" element={<Login />} />
                        <Route path="/signup" element={<Signup />} />

                        {/* Protected Route */}
                        <Route 
                            path="/dashboard" 
                            element={
                                <ProtectedRoute>
                                    <Dashboard />
                                </ProtectedRoute>
                            } 
                        />

                        {/* Catch-all route for unknown paths */}
                        <Route path="*" element={<Navigate to="/login" />} />
                    </Routes>
                </AuthProvider>
            </ErrorBoundary>
        </Router>
    );
};

export default App;
