import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState({ token: null, user: null });
    const [loading, setLoading] = useState(true); // Track loading state
    const navigate = useNavigate();

    // Fetch user data using token
    const fetchUserData = async (token) => {
        try {
            const response = await axios.get('https://api.freeapi.app/api/v1/users/current-user', {
                headers: { Authorization: `Bearer ${token}` },
            });
            setAuth((prev) => ({ ...prev, user: response.data }));
        } catch (error) {
            console.error('Failed to fetch user data:', error);
            handleAuthError(error);
        } finally {
            setLoading(false); // Set loading to false after fetching or error
        }
    };

    // Handle login functionality
    const handleLogin = async (credentials) => {
        try {
            const response = await axios.post('https://api.freeapi.app/api/v1/users/login', credentials);
            const { user, token, refreshToken } = response.data;
            if (!user || !token || !refreshToken) {
                throw new Error('Invalid response format');
            }
            login(user, token, refreshToken);
            navigate('/dashboard');
        } catch (error) {
            console.error('Login failed:', error.response?.data || error.message);
            alert(error.response?.data?.message || 'Login failed. Please try again.');
        }
    };

    // Handle authentication errors (expired or invalid token)
    const handleAuthError = (error) => {
        if (
            error.response &&
            error.response.status === 401 &&
            (error.response.data.message === 'jwt malformed' || error.response.data.message.includes('expired'))
        ) {
            alert('Your session has expired or the token is invalid. Please log in again.');
            logout();
            navigate('/login');
        } else {
            console.error('Unexpected authentication error:', error.message);
        }
    };

    // Perform login action
    const login = (user, token, refreshToken) => {
        setAuth({ token, user });
        localStorage.setItem('token', token);
        localStorage.setItem('user', JSON.stringify(user));
        localStorage.setItem('refreshToken', refreshToken);
        setLoading(false);
    };

    // Perform logout action
    const logout = () => {
        setAuth({ token: null, user: null });
        setLoading(false);
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        localStorage.removeItem('refreshToken');
        navigate('/login');
    };

    // Retrieve token from localStorage
    const getToken = () => {
        return localStorage.getItem('token');
    };

    // Check token on app load and fetch user data
    useEffect(() => {
        const storedToken = localStorage.getItem('token');
        if (storedToken) {
            setAuth((prev) => ({ ...prev, token: storedToken }));
            fetchUserData(storedToken); // Fetch user data if token exists
        } else {
            setLoading(false); // If no token exists, set loading to false
            navigate('/login');
        }
    }, [navigate]);

    // Refresh token every 15 minutes
    useEffect(() => {
        const interval = setInterval(() => {
            refreshToken();
        }, 15 * 60 * 1000); // Refresh token every 15 minutes

        return () => clearInterval(interval); // Cleanup on component unmount
    }, []);

    // Handle token refresh
    const refreshToken = async () => {
        const storedRefreshToken = localStorage.getItem('refreshToken');
        if (!storedRefreshToken) {
            console.error('Refresh token is missing.');
            logout();
            navigate('/login');
            return;
        }
        try {
            const response = await axios.post('https://api.freeapi.app/api/v1/users/refresh-token', { token: storedRefreshToken });
            const { accessToken } = response.data;
            setAuth((prev) => ({ ...prev, token: accessToken }));
            localStorage.setItem('token', accessToken);
        } catch (error) {
            console.error('Token refresh failed:', error);
            logout();
            navigate('/login');
        }
    };

    return (
        <AuthContext.Provider value={{ auth, loading, login, logout, handleLogin }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
