import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import { useAuth } from './AuthContext'; // Import useAuth from context

const Login = () => {
    const navigate = useNavigate(); // Navigate to other routes
    const { login } = useAuth(); // Access login function from context

    // Initial form values
    const initialValues = { email: '', password: '' };

    // Form validation schema
    const validationSchema = Yup.object({
        email: Yup.string().email('Invalid email format').required('Email is required'),
        password: Yup.string().required('Password is required'),
    });

    // Handle form submission
    const handleSubmit = async (values, { setSubmitting, setFieldError }) => {
        console.log('Submitting payload:', values);
        try {
            const response = await fetch('https://api.freeapi.app/api/v1/users/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(values) // Use the Formik values directly
            });
    
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
    
            const data = await response.json();
            console.log('API Response:', data);
    
            const { user, accessToken } = data.data;
    
            if (!user || !accessToken) {
                throw new Error('Invalid API response');
            }
    
            login(user, accessToken); // Save to context
            navigate('/dashboard');
        } catch (error) {
            console.error('Login failed:', error.message);
    
            if (error.message.includes('404')) {
                alert('The login endpoint was not found. Please check the URL.');
            } else if (error.message.includes('401')) {
                alert('Invalid email or password. Please try again.');
            } else {
                alert('An error occurred. Please try again.');
            }
        } finally {
            setSubmitting(false); // Stop form submission spinner
        }
    };
    return (
        <div className="flex items-center justify-center min-h-screen bg-gray-100">
            <div className="w-full max-w-md p-8 bg-white rounded-lg shadow-md">
                <h2 className="text-2xl font-bold text-center mb-6">Login</h2>
                <Formik
                    initialValues={initialValues}
                    validationSchema={validationSchema}
                    onSubmit={handleSubmit}
                >
                    {({ isSubmitting }) => (
                        <Form className="space-y-4">
                            {/* Email Field */}
                            <div>
                                <label htmlFor="email" className="block text-sm font-medium text-gray-700">
                                    Email
                                </label>
                                <Field
                                    type="email"
                                    name="email"
                                    id="email"
                                    placeholder="Enter your email"
                                    className="mt-1 block w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring focus:ring-blue-300"
                                />
                                <ErrorMessage name="email" component="div" className="text-red-500 text-sm" />
                            </div>

                            {/* Password Field */}
                            <div>
                                <label htmlFor="password" className="block text-sm font-medium text-gray-700">
                                    Password
                                </label>
                                <Field
                                    type="password"
                                    name="password"
                                    id="password"
                                    placeholder="Enter your password"
                                    className="mt-1 block w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:ring focus:ring-blue-300"
                                />
                                <ErrorMessage name="password" component="div" className="text-red-500 text-sm" />
                            </div>

                            {/* Submit Button */}
                            <button
                                type="submit"
                                disabled={isSubmitting}
                                className={`w-full py-2 text-white font-bold rounded-md ${
                                    isSubmitting
                                        ? 'bg-blue-300 cursor-not-allowed'
                                        : 'bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring focus:ring-blue-300'
                                }`}
                            >
                                {isSubmitting ? 'Logging in...' : 'Login'}
                            </button>
                        </Form>
                    )}
                </Formik>
            </div>
        </div>
    );
};

export default Login;
