import React, { createContext, useContext, useState, useEffect } from 'react';
import jwtDecode from 'jwt-decode'; // Import jwt-decode
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState({ token: null, user: null });
    const navigate = useNavigate();

    const getToken = () => {
        return localStorage.getItem('token');
    };

    const handleLogin = async (credentials) => {
        try {
            // Mock server response with a JWT token
            const mockToken = 'mock-jwt-token';
            const decodedToken = jwtDecode(mockToken); // Decode the token
            
            const mockUserData = {
                token: mockToken,
                user: {
                    id: decodedToken.id || '1', // Use decoded data
                    email: credentials.email,
                    name: decodedToken.name || 'Test User'
                }
            };

            setAuth({
                token: mockUserData.token,
                user: mockUserData.user,
                isAuthenticated: true
            });

            localStorage.setItem('token', mockUserData.token);
            localStorage.setItem('user', JSON.stringify(mockUserData.user));
            localStorage.setItem('isAuthenticated', true);

            navigate('/dashboard');
            return mockUserData;
        } catch (error) {
            console.error('Error during login:', error.message);
            throw new Error('Login failed');
        }
    };

    const logout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        localStorage.removeItem('isAuthenticated');
        setAuth({ user: null, token: null, isAuthenticated: false });
        navigate('/login', { replace: true });
    };

    useEffect(() => {
        const token = localStorage.getItem('token');
        const user = JSON.parse(localStorage.getItem('user'));
        const isAuthenticated = localStorage.getItem('isAuthenticated');

        if (token && user && isAuthenticated) {
            setAuth({
                token,
                user,
                isAuthenticated: true
            });
        }
    }, []);

    return (
        <AuthContext.Provider value={{
            auth,
            setAuth,
            handleLogin,
            getToken,
            logout
        }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
