import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser ] = useState(null);
    const [token, setToken] = useState(null);
    const [loading, setLoading] = useState(false); // Define loading state

    const fetchUserData = async (token) => {
        setLoading(true);
        try {
            const response = await axios.get('https://api.freeapi.app/api/v1/users/current-user', {
                headers: { Authorization: `Bearer ${token}` },
            });
            setUser (response.data); // Set user data directly
        } catch (error) {
            console.error('Failed to fetch user data:', error);
            handleAuthError(error);
        } finally {
            setLoading(false);
        }
    };

    const handleAuthError = (error) => {
        if (
            error.response &&
            error.response.status === 401 &&
            (error.response.data.message === 'jwt malformed' || error.response.data.message.includes('expired'))
        ) {
            alert('Your session has expired or the token is invalid. Please log in again.');
            logout();
        } else {
            console.error('Unexpected authentication error:', error.message);
        }
    };

    const handleLogin = async (credentials) => {
        console.log('Login credentials:', credentials);
        try {
            const response = await axios.post('https://api.freeapi.app/api/v1/users/login', credentials);
            console.log('Login response:', response.data);

            // Extract and set the token and user
            const { user, token } = response.data;
            if (!user || !token) {
                throw new Error('Invalid response format');
            }

            login(user, token); // Save in context
        } catch (error) {
            console.error('Login failed:', error.response?.data || error.message);
            alert(error.response?.data?.message || 'Login failed. Please try again.');
        }
    };

    const login = (userData, accessToken) => {
        setUser (userData);
        setToken(accessToken);
        setLoading(false); // Set loading to false after login
        if (!accessToken || accessToken.trim() === '') {
            console.error('Invalid token provided during login.');
            alert('Login failed. Please try again.');
            return;
        }

        console.log('Setting auth state with user and token:', { user: userData, token: accessToken }); // Debug log
        localStorage.setItem('token', accessToken); // Persist token
        localStorage.setItem('user', JSON.stringify(userData)); // Persist user data

        fetchUserData(accessToken);
    };

    const logout = () => {
        setUser (null);
        setToken(null);
        setLoading(false); // Set loading to false on logout
        localStorage.removeItem('token');
        localStorage.removeItem('user');
    };

    const refreshToken = async () => {
        const refreshToken = localStorage.getItem('refreshToken');
        try {
            const response = await axios.post('https://api.freeapi.app/api/v1/users/refresh-token', { token: refreshToken });
            const { accessToken } = response.data;

            // Update token in context and localStorage
            setToken(accessToken);
            localStorage.setItem('token', accessToken);
        } catch (error) {
            console.error('Token refresh failed:', error);
            logout(); // Log out if refresh fails
        }
    };

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            fetchUserData(token);
        }
    }, []);

    useEffect(() => {
        const interval = setInterval(() => {
            refreshToken();
        }, 15 * 60 * 1000); // Refresh token every 15 minutes

        return () => clearInterval(interval);
    }, []);

    return (
        <AuthContext.Provider value={{ user, token, loading, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
