import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

const safeParse = (data) => {
    try {
        return JSON.parse(data);
    } catch (error) {
        console.error('Failed to parse JSON:', error);
        return null;
    }
};

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState({ token: null, user: null });
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    const getToken = () => {
        return localStorage.getItem('token');
    };

    const fetchUserData = async () => {
        const token = getToken();
        if (!token) {
            console.error('No token found. Cannot fetch user data.');
            return;
        }
        try {
            const response = await axios.get('https://api.freeapi.app/api/v1/users/current-user', {
                headers: { Authorization: `Bearer ${token}` },
            });
            setAuth((prev) => ({ ...prev, user: response.data }));
        } catch (error) {
            console.error('Failed to fetch user data:', error);
            logout(); // Logout if fetching user data fails
        }
    };

    const logout = () => {
        localStorage.removeItem('token');
        setAuth({ user: null, token: null });
        navigate('/login'); // Redirect to login page after logout
    };

    useEffect(() => {
        const token = getToken();
        if (token) {
            fetchUserData();
            setAuth({ token });
        } else {
            navigate('/login'); // Redirect to login if no token
        }
        setLoading(false);
    }, [navigate]);

    return (
        <AuthContext.Provider value={{ auth, loading, logout, getToken }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
