import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState({ token: null, user: null });
    const [loading, setLoading] = useState(true); // Track loading state
    const navigate = useNavigate();

    // Fetch user data using token
    const fetchUserData = async (token) => {
        try {
            const response = await axios.get('https://api.freeapi.app/api/v1/users/current-user', {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            console.log('User data:', response.data);
        } catch (error) {
            console.error('Failed to fetch user data:', error);
        }
    };    

    // Handle login functionality
    const handleLogin = async (loginData) => {  // Accept loginData as parameter
        try {
            const response = await axios.post('https://api.freeapi.app/api/v1/login', loginData);  // Use loginData here
            const { token, user, refreshToken } = response.data;  // Extract user data and token
            console.log('Received token:', token);
            if (token && user) {
                login(user, token, refreshToken);  // Call login with the user data
            } else {
                console.error('No token or user data received.');
            }
        } catch (error) {
            console.error('Login failed:', error.response?.data || error.message);
            alert(error.response?.data?.message || 'Login failed. Please try again.');
        }
    };
    

    // Handle authentication errors (expired or invalid token)
    const handleAuthError = (error) => {
        if (
            error.response &&
            error.response.status === 401 &&
            (error.response.data.message === 'jwt malformed' || error.response.data.message.includes('expired'))
        ) {
            alert('Your session has expired or the token is invalid. Please log in again.');
            localStorage.removeItem('token'); // Remove corrupted token
            logout(); // Ensure proper logout
            navigate('/login');
        } else {
            console.error('Unexpected authentication error:', error.message);
        }
    };

    // Perform login action
    const login = async (user, token, refreshToken) => {
        if (user && token) {
            localStorage.setItem('token', token);
            localStorage.setItem('user', JSON.stringify(user));
            localStorage.setItem('refreshToken', refreshToken);
            setAuth({ token, user });
            console.log('Token and user stored in localStorage:', token, user);
        } else {
            console.error('Token or user is missing, cannot store.');
        }
    };

    // Perform logout action
    const logout = () => {
        setAuth({ token: null, user: null });
        setLoading(false);
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        localStorage.removeItem('refreshToken');
        navigate('/login');
    };

    // Retrieve token from localStorage
    const getToken = () => {
        const token = localStorage.getItem('token');
        console.log('Retrieved token from localStorage:', token);  // Debugging
        return token;
    };
    

    // Check token on app load and fetch user data
    useEffect(() => {
        const storedToken = getToken(); // Use getToken here
        console.log('Stored token:', storedToken);  // Debugging
        if (storedToken) {
            setAuth((prev) => ({ ...prev, token: storedToken }));
            fetchUserData(storedToken); // Proceed if token is valid
        } else {
            console.log('No token found, redirecting to login');  // Debugging
            setLoading(false);
            navigate('/login'); // Redirect to login
        }
    }, [navigate]);


    // Refresh token every 15 minutes
    useEffect(() => {
        const interval = setInterval(() => {
            refreshToken();
        }, 15 * 60 * 1000); // Refresh token every 15 minutes

        return () => clearInterval(interval); // Cleanup on component unmount
    }, []);

    // Handle token refresh
    const refreshToken = async () => {
        const storedRefreshToken = getToken('refreshToken'); // Retrieve refresh token correctly
        if (!storedRefreshToken) {
            console.error('Refresh token is missing.');
            logout();
            navigate('/login');
            return;
        }
        try {
            const response = await axios.post('https://api.freeapi.app/api/v1/users/refresh-token', { token: storedRefreshToken });
            const { accessToken } = response.data;
            localStorage.setItem('token', accessToken);
            setAuth((prev) => ({ ...prev, token: accessToken }));
        } catch (error) {
            console.error('Token refresh failed:', error);
            logout();
            navigate('/login');
        }
    };

    return (
        <AuthContext.Provider value={{ auth, loading, login, logout, handleLogin }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
