import React, { useState, useEffect } from 'react';
import { useAuth } from './AuthContext';
import axios from 'axios';

const Dashboard = () => {
    const { auth } = useAuth();
    const [data, setData] = useState({
        quote: null,
        joke: null
    });
    const [loading, setLoading] = useState({
        quote: true,
        joke: true
    });
    const [errors, setErrors] = useState({
        quote: null,
        joke: null
    });

    const fetchQuote = async () => {
        try {
            setLoading(prev => ({ ...prev, quote: true }));
            const response = await axios.get('https://api.freeapi.app/api/v1/quotes/random');
            setData(prev => ({ ...prev, quote: response.data?.data }));
            setErrors(prev => ({ ...prev, quote: null }));
        } catch (error) {
            setErrors(prev => ({
                ...prev,
                quote: 'Failed to fetch quote. Please try again.'
            }));
        } finally {
            setLoading(prev => ({ ...prev, quote: false }));
        }
    };

    const fetchJoke = async () => {
        try {
            setLoading(prev => ({ ...prev, joke: true }));
            const response = await axios.get('https://api.freeapi.app/api/v1/jokes/random');
            setData(prev => ({ ...prev, joke: response.data?.data }));
            setErrors(prev => ({ ...prev, joke: null }));
        } catch (error) {
            setErrors(prev => ({
                ...prev,
                joke: 'Failed to fetch joke. Please try again.'
            }));
        } finally {
            setLoading(prev => ({ ...prev, joke: false }));
        }
    };

    useEffect(() => {
        fetchQuote();
        fetchJoke();
    }, []);

    return (
        <div className="max-w-4xl mx-auto p-4">
            {/* Quote Section */}
            <div className="mb-8 bg-white rounded-lg shadow-md p-6">
                <h2 className="text-xl font-bold mb-4">Daily Quote</h2>
                {loading.quote ? (
                    <div className="flex justify-center items-center h-24">
                        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
                    </div>
                ) : errors.quote ? (
                    <div className="text-red-500 p-4 bg-red-50 rounded">
                        {errors.quote}
                        <button
                            onClick={fetchQuote}
                            className="ml-4 text-sm text-blue-500 hover:underline"
                        >
                            Retry
                        </button>
                    </div>
                ) : (
                    <blockquote className="italic text-gray-700">
                        "{data.quote?.content}"
                        <footer className="text-sm text-gray-500 mt-2">
                            - {data.quote?.author || 'Unknown'}
                        </footer>
                    </blockquote>
                )}
            </div>

            {/* Joke Section */}
            <div className="bg-white rounded-lg shadow-md p-6">
                <h2 className="text-xl font-bold mb-4">Random Joke</h2>
                {loading.joke ? (
                    <div className="flex justify-center items-center h-24">
                        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-green-500"></div>
                    </div>
                ) : errors.joke ? (
                    <div className="text-red-500 p-4 bg-red-50 rounded">
                        {errors.joke}
                        <button
                            onClick={fetchJoke}
                            className="ml-4 text-sm text-blue-500 hover:underline"
                        >
                            Retry
                        </button>
                    </div>
                ) : (
                    <div className="prose">
                        <p className="text-gray-800">{data.joke?.content}</p>
                        <button
                            onClick={fetchJoke}
                            className="mt-4 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition-colors"
                        >
                            Get New Joke
                        </button>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Dashboard;
