import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState({ token: null, user: null });
    const [loading, setLoading] = useState(true); // Start with loading as true until token is checked
    const navigate = useNavigate(); // Replaces useHistory

    // Fetch user data using token
    const fetchUserData = async (token) => {
        try {
            const response = await axios.get('https://api.freeapi.app/api/v1/users/current-user', {
                headers: { Authorization: `Bearer ${token}` },
            });
            setAuth((prev) => ({
                ...prev,
                user: response.data, // Set user data directly
            }));
        } catch (error) {
            console.error('Failed to fetch user data:', error);
            handleAuthError(error);
        } finally {
            setLoading(false); // Ensure loading is set to false once data is fetched or an error occurs
        }
    };

    // Handle login functionality
    const handleLogin = async (credentials) => {
        try {
            const response = await axios.post('https://api.freeapi.app/api/v1/users/login', credentials);
            console.log('Login response:', response.data);
            const { user, token, refreshToken } = response.data;
            if (!user || !token || !refreshToken) {
                throw new Error('Invalid response format');
            }
            login(user, token, refreshToken);
            navigate('/dashboard'); // Redirect to the dashboard or any other protected route
        } catch (error) {
            console.error('Login failed:', error.response?.data || error.message);
            alert(error.response?.data?.message || 'Login failed. Please try again.');
        }
    };

    // Handle authentication errors
    const handleAuthError = (error) => {
        if (
            error.response &&
            error.response.status === 401 &&
            (error.response.data.message === 'jwt malformed' || error.response.data.message.includes('expired'))
        ) {
            alert('Your session has expired or the token is invalid. Please log in again.');
            logout();
            navigate('/login'); // Redirect to login page if token is invalid or expired
        } else {
            console.error('Unexpected authentication error:', error.message);
        }
    };

    // Perform login action
    const login = (user, token, refreshToken) => {
        try {
            setAuth({ token, user });
            localStorage.setItem('token', token); // Store token in localStorage
            localStorage.setItem('user', JSON.stringify(user)); // Store user in localStorage (if needed)
            localStorage.setItem('refreshToken', refreshToken); // Store refresh token in localStorage
            setLoading(false); // Set loading to false after successful login
        } catch (error) {
            console.error('Login failed:', error);
        }
    };

    // Perform logout action
    const logout = () => {
        setAuth({ token: null, user: null });
        setLoading(false); // Ensure loading is false when logged out
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        localStorage.removeItem('refreshToken'); // Remove refresh token
        navigate('/login'); // Redirect to login page after logout
    };

    // Retrieve token from localStorage
    const getToken = () => {
        const token = localStorage.getItem('token');
        return token;
    };

    // Check token on app load
    useEffect(() => {
        const storedToken = localStorage.getItem('token');
        if (storedToken) {
            setAuth((prev) => ({
                ...prev,
                token: storedToken,
            }));
            fetchUserData(storedToken); // Fetch user data if token exists
        } else {
            setLoading(false);  // Mark loading as complete if no token exists
            navigate('/login'); // Redirect to login if no token is found
        }
    }, [history]);

    // Refresh token every 15 minutes
    useEffect(() => {
        const interval = setInterval(() => {
            refreshToken();
        }, 15 * 60 * 1000); // Refresh token every 15 minutes

        return () => clearInterval(interval);
    }, []);

    // Handle token refresh
    const refreshToken = async () => {
        const storedRefreshToken = localStorage.getItem('refreshToken');
        if (!storedRefreshToken) {
            console.error('Refresh token is missing.');
            logout(); // Log out if refresh token is missing
            navigate('/login'); // Redirect to login if no refresh token exists
            return;
        }
        try {
            const response = await axios.post('https://api.freeapi.app/api/v1/users/refresh-token', { token: storedRefreshToken });
            const { accessToken } = response.data;
            setAuth((prev) => ({
                ...prev,
                token: accessToken,
            }));

            // Update token in localStorage
            localStorage.setItem('token', accessToken);
        } catch (error) {
            console.error('Token refresh failed:', error);
            logout(); // Log out if refresh fails
            navigate('/login'); // Redirect to login after failed refresh
        }
    };

    return (
        <AuthContext.Provider value={{ auth, loading, login, logout, handleLogin }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
