import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState({ token: null, user: null });
    const navigate = useNavigate();

    const getToken = () => {
        return localStorage.getItem('token');
    };

    const handleLogin = async (credentials) => {
        try {
            console.log('Attempting to login with credentials:', credentials);
            const response = await axios.post('https://api.freeapi.app/api/v1/auth/login', credentials);
            console.log('Login response:', response.data); // Log the entire response

            // Check if the response is in JSON format
            if (typeof response.data !== 'object') {
                throw new Error('Unexpected response format');
            }
            const userData = response.data.data;

            if (!userData || !userData.token) {
                throw new Error('Invalid login response');
            }

            // Set authentication state
            setAuth({
                token: userData.token,
                user: userData.user ?? undefined,
                isAuthenticated: true
            });

            return userData;
        } catch (error) {
            console.error('Login error:', error.response?.data || error.message);
            throw new Error(error.response?.data?.message || 'Login failed.');
        }
    };





    const logout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        localStorage.removeItem('isAuthenticated');
        setAuth({ user: null, token: null, isAuthenticated: false });
        navigate('/login', { replace: true });
    };

    useEffect(() => {
        const token = localStorage.getItem('token');
        const user = JSON.parse(localStorage.getItem('user'));
        const isAuthenticated = localStorage.getItem('isAuthenticated');

        if (token && user && isAuthenticated) {
            setAuth({
                token,
                user,
                isAuthenticated: true
            });
        }
    }, []);

    return (
        <AuthContext.Provider value={{
            auth,
            setAuth,
            handleLogin,
            getToken,
            logout
        }}>
            {children}
        </AuthContext.Provider>
    );
};
export const useAuth = () => useContext(AuthContext);
