import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState({ token: null, user: null });
    const [serverError, setServerError] = useState(null);
    const navigate = useNavigate();

    const getToken = () => {
        return localStorage.getItem('token');
    };

    const handleLogin = async (credentials) => {
        try {
            const response = await axios.post('https://api.freeapi.app/api/v1/users/login', credentials);
            const { user, accessToken: token } = response.data.data;
    
            localStorage.setItem('user', JSON.stringify(user));
            localStorage.setItem('token', token);
            localStorage.setItem('userId', user._id);
            
            setAuth({
                token,
                user,
                isAuthenticated: true
            });
            
            navigate('/dashboard');
            
        } catch (error) {
            console.error('Login failed:', error);
            setServerError(error.response?.data?.message || 'Login failed. Please try again later.');
        }
    };
    

    const logout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        localStorage.removeItem('isAuthenticated');
        localStorage.removeItem('userId');
        setAuth({ user: null, token: null, isAuthenticated: false });

        navigate('/login');
    };

    useEffect(() => {
        const token = localStorage.getItem('token');
        const user = JSON.parse(localStorage.getItem('user'));

        if (token && user) {
            setAuth({
                token,
                user,
                isAuthenticated: true
            });
        } else {
            setAuth({
                token: null,
                user: null,
                isAuthenticated: false
            });
        }
    }, []);

    return (
        <AuthContext.Provider value={{
            auth,
            setAuth,
            handleLogin,
            getToken,
            logout,
            serverError,
            setServerError
        }}>
            {children}
        </AuthContext.Provider>
    );
};
export const useAuth = () => useContext(AuthContext);
