import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState({ user: null, token: null });

    const fetchUserData = async (token) => {
        try {
            const response = await axios.get('https://api.freeapi.app/api/v1/users/current-user', {
                headers: { Authorization: `Bearer ${token}` },
            });
            setAuth((prevAuth) => ({
                ...prevAuth,
                user: response.data,
            }));
        } catch (error) {
            console.error('Failed to fetch user data:', error.message);
            alert('Session expired or user data unavailable. Please log in again.');
            logout();
        } finally {
            setLoading(false); // Mark loading complete
        }
    };

    const login = (user, token) => {
        setAuth({ user, token });
        localStorage.setItem('token', token); // Save token in localStorage
        localStorage.setItem('user', JSON.stringify(user)); // Save user details in localStorage
    };

    const logout = () => {
        setAuth({ user: null, token: null });
        localStorage.removeItem('token'); // Remove token from localStorage
        localStorage.removeItem('user'); // Remove user details from localStorage
    };

    const refreshToken = async () => {
        const token = localStorage.getItem('token');
        if (token) {
            try {
                const response = await axios.post('https://api.freeapi.app/users/refresh-token', { token });
                const newToken = response.data.token;
                setAuth((prev) => ({ ...prev, token: newToken }));
                localStorage.setItem('token', newToken);
            } catch (error) {
                console.error('Token refresh failed:', error.message);
                alert('Session expired. Please log in again.');
                logout();
            }
        }
    };

    // Automatically refresh token every 15 minutes
    useEffect(() => {
        const interval = setInterval(() => {
            refreshToken();
        }, 15 * 60 * 1000); // Refresh token every 15 minutes

        return () => clearInterval(interval);
    }, []);

    return (
        <AuthContext.Provider value={{ auth, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    return useContext(AuthContext);
};
