import React from 'react';
import { Navigate } from 'react-router-dom';
import { useAuth } from './AuthContext';

const ProtectedRoute = ({ children }) => {
    const { auth, loading } = useAuth(); // Destructure loading state from context

    // Show a loading fallback while auth state is being determined
    if (loading) {
        return <div>Loading...</div>; // Replace with a spinner or skeleton loader if preferred
    }

    // Redirect to login if no token is found
    if (!auth?.token) {
        return <Navigate to="/login" />;
    }

    // Render children if user is authenticated
    return children;
};

export default ProtectedRoute;
