import React, { useEffect, useState } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import { useNavigate } from 'react-router-dom';
import { useAuth } from './AuthContext';

const Login = () => {
    const navigate = useNavigate();
    const { handleLogin, getToken } = useAuth();
    const [serverError, setServerError] = useState(''); // Moved inside component

    const initialValues = { email: '', password: '' };
    const validationSchema = Yup.object({
        email: Yup.string().email('Invalid email format').required('Email is required'),
        password: Yup.string().required('Password is required'),
    });

    // Single check for token on component mount
    useEffect(() => {
        const token = getToken();
        if (token) {
            navigate('/dashboard', { replace: true }); // Using replace to prevent back navigation
        }
    }, []); // Remove dependencies to ensure single execution

    const handleSubmit = async (values) => {
        try {
            const userData = await handleLogin(values);
            if (userData) {
                navigate('/dashboard', { replace: true });
            }
        } catch (error) {
            setServerError(error.message);
        }
    };



    return (
        <div className="flex items-center justify-center min-h-screen bg-gray-100">
            <div className="w-full max-w-md p-8 bg-white rounded-lg shadow-md">
                <h2 className="text-2xl font-bold text-center mb-6">Login</h2>
                {serverError && (
                    <div className="mb-4 p-2 text-red-500 bg-red-100 rounded">
                        {serverError}
                    </div>
                )}
                <Formik
                    initialValues={initialValues}
                    validationSchema={validationSchema}
                    onSubmit={(values, actions) => handleSubmit(values, actions)}
                >

                    {({ isSubmitting }) => (
                        <Form className="space-y-4">
                            <div>
                                <label htmlFor="email" className="block text-sm font-medium text-gray-700">Email</label>
                                <Field type="email" name="email" id="email" placeholder="Enter your email" className="mt-1 block w-full p-2 border border-gray-300 rounded-md" />
                                <ErrorMessage name="email" component="div" className="text-red-500 text-sm" />
                            </div>
                            <div>
                                <label htmlFor="password" className="block text-sm font-medium text-gray-700">Password</label>
                                <Field type="password" name="password" id="password" placeholder="Enter your password" className="mt-1 block w-full p-2 border border-gray-300 rounded-md" />
                                <ErrorMessage name="password" component="div" className="text-red-500 text-sm" />
                            </div>
                            <button type="submit" disabled={isSubmitting} className={`w-full py-2 text-white font-bold rounded-md ${isSubmitting ? 'bg-blue-300 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700'}`}>
                                {isSubmitting ? 'Logging in...' : 'Login'}
                            </button>
                        </Form>
                    )}
                </Formik>
            </div>
        </div>
    );
};
export default Login;

// Remove these lines from the bottom of the file
const [serverError, setServerError] = useState('');

{serverError && (
    <div className="mb-4 p-2 text-red-500 bg-red-100 rounded">
        {serverError}
    </div>
)}
