import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState({ token: localStorage.getItem('token') || null, user: JSON.parse(localStorage.getItem('user')) || null, });
    const [serverError, setServerError] = useState(null);
    const navigate = useNavigate();

    const getToken = () => {
        return localStorage.getItem('token');
    };

    const handleLogin = async (credentials) => {
        try {
            const response = await axios.post('https://api.freeapi.app/api/v1/users/login', credentials);
            if (response.data.success) {
                const { token, user } = response.data.data;

                localStorage.setItem('user', JSON.stringify(user));
                localStorage.setItem('token', token);
                localStorage.setItem('userId', user.id);

                setAuth({ token: token, user, userId: user.id });
            
                const storedUserId = localStorage.getItem('userId');
                if (storedUserId && storedUserId === user.id) {
                    localStorage.setItem('user', JSON.stringify(user));
                    localStorage.setItem('token', token);
                    setAuth({
                        token,
                        user,
                        isAuthenticated: true
                    });
                    navigate('/dashboard');
                } else {
                    throw new Error('Login failed. Please check your credentials.');
                }
            }
        } catch (error) {
            console.error('Login Error:', error.message);
            setServerError(error.response?.data?.data?.message || 'Login failed. Please try again later.');
        }
    };

    const logout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        localStorage.removeItem('isAuthenticated');
        localStorage.removeItem('userId');
        setAuth({ user: null, token: null, isAuthenticated: false });

        navigate('/login');
    };

    const validateToken = async () => {
        const token = localStorage.getItem('token');
        if (token) {
            try {
                const response = await axios.post('/validate-token', { token });
                if (response.data?.isValid) {
                    setAuth({
                        token,
                        user: response.data.user,
                    });
                    localStorage.setItem('user', JSON.stringify(response.data.user));
                } else {
                    logout();
                }
            } catch (error) {
                console.error('Token validation failed:', error);
                logout();
            }
        }
    };

    useEffect(() => {
        validateToken();
    }, []);

    useEffect(() => {
        const token = localStorage.getItem('token');
        const user = JSON.parse(localStorage.getItem('user'));

        if (token && user) {
            setAuth({
                token,
                user,
                isAuthenticated: true
            });
        } else {
            setAuth({
                token: null,
                user: null,
                isAuthenticated: false
            });
        }
    }, []);

    return (
        <AuthContext.Provider value={{
            auth,
            setAuth,
            handleLogin,
            getToken,
            logout,
            serverError,
            setServerError
        }}>
            {children}
        </AuthContext.Provider>
    );
};
export const useAuth = () => useContext(AuthContext);
