import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext';
import { useNavigate } from 'react-router-dom';
import ErrorBoundary from './ErrorBoundary';

const Dashboard = () => {
    const { auth, logout } = useAuth();
    const navigate = useNavigate();
    const [dashboardData, setDashboardData] = useState({
        quote: '',
        joke: '',
        loading: true,
        error: null
    });
    const [profile, setProfile] = useState({ name: auth.user?.name || '', email: auth.user?.email || '' });
    const [updatingProfile, setUpdatingProfile] = useState(false);
    const [profileUpdateSuccess, setProfileUpdateSuccess] = useState(null);

    // Single authentication check
    useEffect(() => {
        if (!auth.token) {
            navigate('/login', { replace: true });
            return;
        }

        const fetchDashboardData = async () => {
            try {
                // Single API call for both quote and joke
                const response = await axios.get('https://api.freeapi.app/api/v1/public/quotes', {
                    headers: { Authorization: `Bearer ${auth.token}` }
                });

                setDashboardData({
                    quote: response.data.data?.[0]?.quote || 'No quote available',
                    joke: 'Welcome to your dashboard!', // Static message instead of API call
                    loading: false,
                    error: null
                });
            } catch (error) {
                setDashboardData(prev => ({
                    ...prev,
                    loading: false,
                    error: 'Failed to load dashboard data'
                }));
            }
        };

        fetchDashboardData();
    }, [auth.token]);

    // Rest of your component remains the same
    const handleProfileUpdate = async () => {
        setUpdatingProfile(true);
        setProfileUpdateSuccess(null);
        try {
            const response = await axios.put('https://api.freeapi.app/api/v1/users/update', profile, {
                headers: { Authorization: `Bearer ${auth.token}` },
            });
            setProfileUpdateSuccess('Profile updated successfully!');
        } catch (error) {
            console.error('Profile update failed:', error);
            setProfileUpdateSuccess('Failed to update profile. Please try again.');
        } finally {
            setUpdatingProfile(false);
        }
    };

    return (
        <ErrorBoundary>
            <div className="min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-800 dark:text-white">
                <div className="max-w-4xl mx-auto p-6">
                    <div className="bg-white dark:bg-gray-800 rounded-lg shadow-lg p-6">
                        <h1 className="text-2xl font-bold mb-4">Hello, {auth.user?.name || 'User'}!</h1>
                        {loadingQuote ? (
                            <p>Loading quote...</p>
                        ) : quoteError ? (
                            <div className="mb-4 p-4 bg-red-100 dark:bg-red-900 rounded-lg text-red-800 dark:text-red-200">{quoteError}</div>
                        ) : (
                            <div className="mb-4 p-4 bg-blue-100 dark:bg-blue-900 rounded-lg">
                                <p className="text-lg font-semibold">Quote of the Day:</p>
                                <p>"{quote}"</p>
                            </div>
                        )}
                        {loadingJoke ? (
                            <p>Loading joke...</p>
                        ) : jokeError ? (
                            <div className="mb-4 p-4 bg-red-100 dark:bg-red-900 rounded-lg text-red-800 dark:text-red-200">{jokeError}</div>
                        ) : (
                            <div className="mb-4 p-4 bg-green-100 dark:bg-green-900 rounded-lg">
                                <p className="text-lg font-semibold">Random Joke:</p>
                                <p>"{joke}"</p>
                            </div>
                        )}
                        <div className="bg-gray-100 dark:bg-gray-700 p-4 rounded-lg">
                            <h2 className="text-xl font-bold mb-4">Your Profile</h2>
                            <p><strong>Email:</strong> {auth.user?.email || 'N/A'}</p>
                        </div>
                        <div className="bg-gray-100 dark:bg-gray-700 p-4 rounded-lg mt-6">
                            <h2 className="text-xl font-bold mb-4">Update Profile</h2>
                            <input
                                type="text"
                                value={profile.name}
                                onChange={(e) => setProfile((prev) => ({ ...prev, name: e.target.value }))}
                                className="w-full p-2 border rounded-md dark:bg-gray-800 dark:border-gray-600"
                                placeholder="Update your name"
                            />
                            <button
                                onClick={handleProfileUpdate}
                                disabled={updatingProfile}
                                className="mt-4 bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 transition-all"
                            >
                                {updatingProfile ? 'Updating...' : 'Update Profile'}
                            </button>
                            {profileUpdateSuccess && <p className="mt-4 text-center text-sm">{profileUpdateSuccess}</p>}
                        </div>
                        <button
                            onClick={logout}
                            className="mt-6 w-full bg-red-600 text-white py-2 px-4 rounded-md hover:bg-red-700 transition-all"
                        >
                            Logout
                        </button>
                    </div>
                </div>
            </div>
        </ErrorBoundary>
    );
};

export default Dashboard;
