import React, { Component } from 'react';

class ErrorBoundary extends Component {
    constructor(props) {
        super(props);
        this.state = { hasError: false }; // Track if an error has occurred
    }

    static getDerivedStateFromError(error) {
        // When an error is caught, update the state to display fallback UI
        return { hasError: true };
    }

    componentDidCatch(error, info) {
        // Log the error to an error reporting service (optional)
        console.error("Error caught in ErrorBoundary:", error);
        console.error("Error info:", info);
    }

    render() {
        // Fallback UI in case of error
        if (this.state.hasError) {
            return (
                <div style={{ padding: '20px', backgroundColor: '#f8d7da', color: '#721c24' }}>
                    <h1>Something went wrong. Please try again later.</h1>
                </div>
            );
        }

        // Render children if no error
        return this.props.children;
    }
}

export default ErrorBoundary;
