import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState({ token: null, user: null });

    // Load token from localStorage on initial load
    useEffect(() => {
        const storedToken = localStorage.getItem('token');
        if (storedToken) {
            setAuth((prev) => ({ ...prev, token: storedToken }));
            // Fetch user data if token is available
            fetchUserData(storedToken);
        }
    }, []);    

    const fetchUserData = async (token) => {
        try {
            const response = await axios.get('https://api.freeapi.app/users/current-user', {
                headers: { Authorization: `Bearer ${token}` },
            });
            const user = response.data;
            setAuth((prevAuth) => ({
                ...prevAuth,
                user,
            }));
        } catch (error) {
            console.error('Failed to fetch user data:', error.message);
            logout(); // Log out if fetching user data fails
        }
    };
    

    const login = (user, token) => {
        setAuth({ user, token });
        localStorage.setItem('token', token);
    };

    const logout = () => {
        setAuth({ user: null, token: null });
        localStorage.removeItem('token');
    };

    const refreshToken = async () => {
        const token = localStorage.getItem('token');
        if (token) {
            try {
                const response = await axios.post('https://api.freeapi.app/users/refresh-token', { token });
                setAuth((prev) => ({ ...prev, token: response.data.token }));
                localStorage.setItem('token', response.data.token);
            } catch (error) {
                console.error('Token refresh failed:', error);
                logout(); // Logout if refresh fails
            }
        }
    };

    useEffect(() => {
        const interval = setInterval(() => {
            refreshToken();
        }, 15 * 60 * 1000); // Refresh token every 15 minutes

        return () => clearInterval(interval);
    }, []);

    return (
        <AuthContext.Provider value={{ auth, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    return useContext(AuthContext);
};
