import React from 'react';
import { Navigate } from 'react-router-dom';
import { useAuth } from './AuthContext';

const ProtectedRoute = ({ children }) => {
    const { auth, loading } = useAuth();  // Destructure both auth and loading from useAuth
    console.log('Token in ProtectedRoute:', localStorage.getItem('token')); // Log token here

    if (loading) {
        return <div>Loading...</div>;  // Shows loading while auth state is being fetched
    }

    if (!auth?.token) {
        return <Navigate to="/login" />;
    }

    return children;
};

export default ProtectedRoute;
