import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext';
import { useNavigate } from 'react-router-dom';
import ErrorBoundary from './ErrorBoundary';

const Dashboard = () => {
    const { auth, logout } = useAuth();
    const navigate = useNavigate();
    const [quote, setQuote] = useState('');
    const [joke, setJoke] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [profile, setProfile] = useState({ 
        name: auth.user?.name || '', 
        email: auth.user?.email || '' 
    });
    const [updatingProfile, setUpdatingProfile] = useState(false);
    const [profileUpdateSuccess, setProfileUpdateSuccess] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            if (!auth.token) {
                navigate('/login', { replace: true });
                return;
            }

            try {
                const quoteResponse = await axios.get('https://api.freeapi.app/api/v1/public/quotes', {
                    headers: { Authorization: `Bearer ${auth.token}` }
                });
                setQuote(quoteResponse.data.data?.[0]?.quote || 'No quote available');

                const jokeResponse = await axios.get('https://api.freeapi.app/api/v1/public/randomjokes', {
                    headers: { Authorization: `Bearer ${auth.token}` }
                });
                setJoke(jokeResponse.data.data?.body || 'No joke available');

                setLoading(false);
            } catch (error) {
                setError('Failed to load content');
                setLoading(false);
            }
        };

        fetchData();
    }, [auth.token, navigate]);

    const handleProfileUpdate = async () => {
        setUpdatingProfile(true);
        setProfileUpdateSuccess(null);
        try {
            const response = await axios.put('https://api.freeapi.app/api/v1/users/update', profile, {
                headers: { Authorization: `Bearer ${auth.token}` },
            });
            setProfileUpdateSuccess('Profile updated successfully!');
        } catch (error) {
            console.error('Profile update failed:', error);
            setProfileUpdateSuccess('Failed to update profile. Please try again.');
        } finally {
            setUpdatingProfile(false);
        }
    };

    return (
        <ErrorBoundary>
            <div className="min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-800 dark:text-white">
                <div className="max-w-4xl mx-auto p-6">
                    <div className="bg-white dark:bg-gray-800 rounded-lg shadow-lg p-6">
                        <h1 className="text-2xl font-bold mb-4">Hello, {auth.user?.name || 'User'}!</h1>
                        {loading ? (
                            <p>Loading content...</p>
                        ) : error ? (
                            <p className="text-red-500">{error}</p>
                        ) : (
                            <>
                                <div className="mb-4 p-4 bg-blue-100 dark:bg-blue-900 rounded-lg">
                                    <h2 className="text-lg font-semibold">Quote of the Day:</h2>
                                    <p>{quote}</p>
                                </div>
                                <div className="mb-4 p-4 bg-green-100 dark:bg-green-900 rounded-lg">
                                    <h2 className="text-lg font-semibold">Random Joke:</h2>
                                    <p>{joke}</p>
                                </div>
                            </>
                        )}
                        <button
                            onClick={logout}
                            className="mt-6 w-full bg-red-600 text-white py-2 px-4 rounded-md hover:bg-red-700 transition-all"
                        >
                            Logout
                        </button>
                    </div>
                </div>
            </div>
        </ErrorBoundary>
    );
};

export default Dashboard;
