import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState({ token: null, user: null });
    const navigate = useNavigate();

    const getToken = () => {
        return localStorage.getItem('token');
    };

    const handleLogin = async (credentials) => {
        try {
            // Validate credentials before making the request
            if (!credentials.email || !credentials.password) {
                throw new Error('Email and password are required');
            }

            const response = await axios.post(
                'https://api.freeapi.app/api/v1/auth/login',
                credentials,
                {
                    headers: {
                        'Content-Type': 'application/json',
                        'Accept': 'application/json'
                    }
                }
            );
            
            // Check for successful response
            if (response?.data?.status === 'success' && response?.data?.data?.token) {
                const { token, user } = response.data.data;
                
                setAuth({ token, user });
                localStorage.setItem('token', token);
                localStorage.setItem('user', JSON.stringify(user));
                
                return { token, user };
            }

            throw new Error(response?.data?.message || 'Authentication failed');
        } catch (error) {
            const errorMessage = error.response?.data?.message || 'Please verify your credentials and try again';
            console.error('Login error:', errorMessage);
            throw new Error(errorMessage);
        }
    };
    const logout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        setAuth({ user: null, token: null });
        navigate('/login');
    };

    useEffect(() => {
        const token = getToken();
        if (token) {
            setAuth({ token });
            // Optionally fetch user data here
        }
    }, []);

    return (
        <AuthContext.Provider value={{ auth, handleLogin, logout, getToken }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
